import { Card, Deck} from '../components'
import { Link } from "react-router-dom";
import React, { useState } from 'react';
import '../styles/home.css';

let flashCardDeck = Card(null,null);
let index = -1;

function addCard(x,y){
  index++;
  flashCardDeck[index] = Card(x,y);
}


export default function Home() {
    const [question, setQuestion] = useState(" ");
    const [indexDisplay, setIndex] = useState(" ");
    const [answer, setAnswer] = useState(" ");
    let [cardText, setText] = useState(" ");
  
    const updateQuestion = event => {
      setQuestion(event.target.value);
    };
    const updateAnswer = event => {
      setAnswer(event.target.value);
    };
    const updateNewCardText = event => {
      setText(flashCardDeck[index].question);
    };
    const updateText = event => {
      if(cardText == flashCardDeck[index].question){
        setText(flashCardDeck[index].answer);
      }
      else{
        setText(flashCardDeck[index].question);
      }
    };
    const updateIndex = event => {
      setIndex(index);
    }
    const handleLeftClick = (type) => {
      if(index <= 0){
        return;
      }
      else{
        index = index - 1;
        updateNewCardText();
      }
    };
    const handleRightClick = (type) => {
      if(flashCardDeck.length - 1 >= index){
        return;
      }
      else{
        index = index + 1;
        updateNewCardText()
      }

    };

      return (
        <>
        <div>
        <input  onChange={updateQuestion} placeholder = "Enter Question"/>
        <input  onChange={updateAnswer} placeholder = "Enter Answer"/>  
        </div>
        <button onClick={()=> {addCard(question,answer); updateNewCardText()}}> Click to add card </button>
        <br />
        <div class = "cardButton" onClick={()=> updateText()}>{cardText}</div>
        <button onClick={() => handleLeftClick()}>{"<"}</button>
        <button onClick={() => handleRightClick()}>{">"}</button>
        <button onClick={() => {flashCardDeck =  []; index = -1;setText(null)}}>{"Empty List"}</button>
        <div>{() =>updateIndex()}</div>
        </>
      )
  }
