import { Card, Deck} from './components'
import './App.css';
import React, { useState } from 'react';
import styled from 'styled-components';

let flashCardDeck = Deck("","","","");

function clickMe() {
  alert("You clicked me!");
}

const StyledInput = styled.input`
  display: block;
  margin: 20px 0px;
  border: 1px solid lightblue;
`;

const Button = styled.button`
  background-color: black;
  color: white;
  font-size: 20px;
  padding: 10px 60px;
  border-radius: 5px;
  margin: 10px 0px;
  cursor: pointer;
`;

function useInput(defaultValue) {
  const [value, setValue] = useState(defaultValue);
  function onChange(e) {
    setValue(e.target.value);
  }
  return {
    value,
    onChange,
  };
}

function addCard(x,y){
  let flashCard = Card(x,y);
  flashCardDeck.card = flashCard;
}


export default function App() {
  const questionProp = useInput();
  const answerProp = useInput();
  return (
    <>
    <div>
    <StyledInput {...questionProp} placeholder = "Enter Question"/>
    <StyledInput {...answerProp} placeholder = "Enter Answer"/>       
    </div>
    <button onClick={console.log("working?")}> Button</button>
    </>
  );
}
