import { Card, Deck} from './components'
import './App.css';
import React, { useState } from 'react';
import styled from 'styled-components';

let flashCardDeck = Deck("","","","");

const handleClick = ()=>{
  flashCardDeck.card.question = questionProp.input
  flashCardDeck.card.answer = answerProp.input
}

const StyledInput = styled.input`
  display: block;
  margin: 20px 0px;
  border: 1px solid lightblue;
`;


function useInput(defaultValue) {
  const [value, setValue] = useState(defaultValue);
  function onChange(e) {
    setValue(e.target.value);
  }
  return {
    value,
    onChange,
  };
}

function addCard(x,y){
  let flashCard = Card(x,y);
  flashCardDeck.card = flashCard;
}


export default function App() {
  const questionProp = useInput();
  const answerProp = useInput();
    return (
      <>
      <div>
      <StyledInput {...questionProp} placeholder = "Enter Question"/>
      <StyledInput {...answerProp} placeholder = "Enter Answer"/>       
      </div>
      <button onClick={handleClick}> Button</button>
      <text>{"Question = " + flashCardDeck.card.question}</text>
      </>
    )
}
