import { Card, Deck} from './components'
import './App.css';
import React, { useState } from 'react';
import styled from 'styled-components';

let flashCardDeck = Deck("","","");
let addingCard = Card("","");



const StyledInput = styled.input`
  display: block;
  margin: 20px 0px;
  border: 1px solid lightblue;
`;



function addCard(x,y){
  flashCardDeck.card.question = x;
  flashCardDeck.card.answer = y;
}


export default function App() {

  const [question, setQuestion] = useState(" ");
  const [answer, setAnswer] = useState(" ");

  const updateQuestion = event => {
    setQuestion(event.target.value);
  };
  const updateAnswer = event => {
    setAnswer(event.target.value);
  };
    return (
      <>
      <div>
      <input  onChange={updateQuestion} placeholder = "Enter Question"/>
      <input  onChange={updateAnswer} placeholder = "Enter Answer"/>       
      </div>
      <button onClick={()=> addCard(question,answer)}> Button</button>
      <text>{"Question = " + flashCardDeck.card.question}</text>
      </>
    )
}
