import { Card, Deck} from '../components'
import { Link } from "react-router-dom";
import React, { useState } from 'react';

let flashCardDeck = Card("Blank Card Question","Blank Card Answer",null,null);

function addCard(x,y){
  if(flashCardDeck.previousCard == null){
    flashCardDeck.question = x;
    flashCardDeck.answer = y;
  }
  else{
    let flashCardDeckCopy = flashCardDeck;
    flashCardDeck = Card(x,y,null,flashCardDeckCopy);
    flashCardDeckCopy.nextCard = flashCardDeck;
    flashCardDeck.previousCard = flashCardDeckCopy;
  }
}


export default function Home() {
    const [question, setQuestion] = useState(" ");
    const [answer, setAnswer] = useState(" ");
    let [cardText, setText] = useState(" ");
  
    const updateQuestion = event => {
      setQuestion(event.target.value);
    };
    const updateAnswer = event => {
      setAnswer(event.target.value);
    };
    const updateNewCardText = event => {
      setText(flashCardDeck.question);
    };
    const updateText = event => {
      if(cardText == flashCardDeck.question){
        setText(flashCardDeck.answer);
      }
      else{
        setText(flashCardDeck.question);
      }
    };
    const handleLeftClick = (type) => {
      let flashCardDeckCopy = flashCardDeck;
      if(flashCardDeck.previousCard == null){
        return;
      }
      else{
        flashCardDeck = flashCardDeckCopy.previousCard;
        flashCardDeck.nextCard = flashCardDeckCopy;
        updateNewCardText();
      }
    };
    const handleRightClick = (type) => {
      let flashCardDeckCopy = flashCardDeck;
      if(flashCardDeck.nextCard == null){
        return;
      }
      else{
        flashCardDeck = flashCardDeckCopy.nextCard;
        flashCardDeck.previousCard = flashCardDeckCopy;
        updateNewCardText()
      }

    };

      return (
        <>
        <div>
        <input  onChange={updateQuestion} placeholder = "Enter Question"/>
        <input  onChange={updateAnswer} placeholder = "Enter Answer"/>  
        </div>
        <button onClick={()=> {addCard(question,answer); updateNewCardText()}}> Click to add card </button>
        <br />
        <div onClick={()=> updateText()}>{cardText}</div>
        <button onClick={() => handleLeftClick()}>{"<"}</button>
        <button onClick={() => handleRightClick()}>{">"}</button>
        </>
      )
  }
