import { Card, Deck} from './components'
import './App.css';
import React, { useState } from 'react';
import styled from 'styled-components';

let flashCardDeck = Deck("","","");
let addingCard = Card("","");

const StyledInput = styled.input`
  display: block;
  margin: 20px 0px;
  border: 1px solid lightblue;
`;

updateQuestion = event => {
  addingCard.question = event.target.value
};
updateAnswer = event => {
  addingCard.answer = event.target.value
};

function addCard(addingCard){
  flashCardDeck.card = addingCard
}


export default function App() {
    return (
      <>
      <div>
      <input  onChange={updateQuestion()} placeholder = "Enter Question"/>
      <input  onChange={updateAnswer()} placeholder = "Enter Answer"/>       
      </div>
      <button onClick={()=> addCard(addingCard)}> Button</button>
      <text>{"Question = " + flashCardDeck.card.question}</text>
      </>
    )
}
